CREATE TABLE IF NOT EXISTS `udalosti` (`ID` INT(6) UNSIGNED NOT NULL AUTO_INCREMENT, `Typ` TINYINT(1) UNSIGNED NOT NULL, `Nadpis` TEXT NOT NULL, `Zprava` TEXT CHARACTER SET cp1250 COLLATE cp1250_czech_cs, `Kdy` DATETIME NOT NULL, `Splneno` TINYINT(1) UNSIGNED NOT NULL DEFAULT '0', `Upozorneno` TINYINT(1) UNSIGNED NOT NULL DEFAULT '0', `Upozorneni` DATETIME NOT NULL, PRIMARY KEY (`ID`)) ENGINE=MYISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='1.0.0.0';
CREATE TABLE IF NOT EXISTS `svatky` (`Datum` TEXT CHARACTER SET cp1250 COLLATE cp1250_czech_cs NOT NULL, `Jmeno` TEXT CHARACTER SET cp1250 COLLATE cp1250_czech_cs NOT NULL, `Upozornit` TINYINT(4) NOT NULL DEFAULT '0') ENGINE=MYISAM DEFAULT CHARSET=utf8 COMMENT='1.0.0.0';
CREATE TABLE IF NOT EXISTS `vyznamne_dny` (`Datum` TEXT CHARACTER SET cp1250 COLLATE cp1250_czech_cs NOT NULL, `Den` TEXT CHARACTER SET cp1250 COLLATE cp1250_czech_cs NOT NULL, `Poradi` TINYINT(4) NOT NULL, `Upozornit` TINYINT(4) NOT NULL DEFAULT '0') ENGINE=MYISAM DEFAULT CHARSET=utf8 COMMENT='1.0.0.0';